// import logo from './logo.svg';
import React, {useState, useEffect} from "react";
import './App.css';
import axios from "axios";

const RouteSummaryForStop = () =>{
  const fetchRouteSummaryForStop = () => {
    axios.get("http://localhost:8080/GetRouteSummaryForStopResult/0893/").then(res => {
        console.log(res);
    });
  }

  useEffect(() => {
    fetchRouteSummaryForStop();
  }, [])

  return <h1>OCTracker</h1>
}

class StopForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stopNo: '',

    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({stopNo: event.target.value});
  }

  handleSubmit(event) {
    // alert('A stop was submitted: ' + this.state.stopNo);
    event.preventDefault();
    axios.get("http://localhost:8080/GetRouteSummaryForStopResult/0893/").then(res => {
      console.log(res);
    });
  }

  render() {
    return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Stop:
            <input type="text" value={this.state.stopNo} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
    );
  }
}

function App() {
  return (
    <div className="App">
      {/*<RouteSummaryForStop />*/}
      <StopForm />
      {/*<header className="App-header">*/}
