            stopDescription: props.stop.stopDescription,
            stopSelectedBool: false,
            tripsDiv: null,
            tripsForStop: null,
            route: null,
        };

        // this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
         this.handleButton = this.handleButton.bind(this);
