// import logo from './logo.svg';
import React, {useState, useEffect} from "react";
import './App.css';
import axios from "axios";

const RouteSummaryForStop = () =>{
  const fetchRouteSummaryForStop = () => {
    axios.get("http://localhost:8080/GetRouteSummaryForStopResult/0893/").then(res => {
        console.log(res);
    });
  }

  useEffect(() => {
    fetchRouteSummaryForStop();
  }, [])

  return <h1>OCTracker</h1>
}

class StopForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stopNo: '',
      stop: null,
      submitted: false,

    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({stopNo: event.target.value, submitted:false, stop: this.state.stop});
  }

  handleSubmit(event) {
    event.preventDefault();
    //const [stop, setStop] = useState([]);

    axios.get("http://localhost:8080/GetRouteSummaryForStopResult/".concat(this.state.stopNo)).then(res => {
      console.log(res);
      // this.state.submitted = true;
      // this.state.stop = res.data;
      this.setState({stop: res.data, submitted: true});
      // return (
      //     <h1>this.state.stop.data.stopNo</h1> +
      //     <h1>this.state.stop.data.stopDescription</h1> +
      //     <ul>
      //       {this.state.stop.data.routes.route.map(route => (
      //           <li >
      //             {this.state.stop.data.routeNo}
      //           </li>
      //       ))}
      //     </ul>
      // );
    }).catch((error)=> {
      this.setState({stop: null, submitted: false});
      console.log(error);
    });
  }

  render() {

    const submitted = this.state.submitted;
    let routeDiv = null;
    if(submitted === true){
      routeDiv = <RouteDiv stop={this.state.stop}/>
    }
    else{
      routeDiv = null;
    }

    return (
        <div>
            <div>
              <form onSubmit={this.handleSubmit}>
                <label>
                  Stop:
                  <input type="text" value={this.state.stopNo} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
              </form>
            </div>
            <div>{routeDiv}</div>
        </div>
    );
  }
}

function RouteDiv(props){
    const stopNo = props.stop.stopNo;
    const stopDescription = props.stop.stopDescription;
    if(stopDescription != null){
      return (<div><h1>{stopNo}</h1>
            <h1>{stopDescription}</h1>
            <ul>
              {props.stop.routes.route.map(route => (
                  <li key={route.routeNo}>
                    {route.routeNo}
                  </li>
              ))}
            </ul>
          </div>
      );
    }
    else{
      return (<div><h1>Invalid stop number</h1></div>
    );
  }
}

function App() {
  return (
    <div className="App">
      {/*<RouteSummaryForStop />*/}
      <StopForm />
      {/*<header className="App-header">*/}
      {/*  <img src={logo} className="App-logo" alt="logo" />*/}
      {/*  <p>*/}
      {/*    Edit <code>src/App.js</code> and save to reload.*/}
      {/*  </p>*/}
      {/*  <a*/}
      {/*    className="App-link"*/}
      {/*    href="https://reactjs.org"*/}
      {/*    target="_blank"*/}
      {/*    rel="noopener noreferrer"*/}
      {/*  >*/}
      {/*    Learn React */}
      {/*  </a>*/}
      {/*</header>*/}
    </div>
  );
}

export default App;
