class StopForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stopNo: '',
      stop: null,
      submitted: false,

    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({stopNo: event.target.value, submitted:false, stop: this.state.stop});
  }

  handleSubmit(event) {
    event.preventDefault();
    //const [stop, setStop] = useState([]);
    if(this.state.stopNo === ''){
        this.setState({stopNo: 0});
    }
    axios.get("http://localhost:8080/GetRouteSummaryForStopResult/".concat(this.state.stopNo)).then(res => {
      console.log(res);
      // this.state.submitted = true;
      // this.state.stop = res.data;
      this.setState({stop: res.data, submitted: true});
      // return (
      //     <h1>this.state.stop.data.stopNo</h1> +
      //     <h1>this.state.stop.data.stopDescription</h1> +
      //     <ul>
      //       {this.state.stop.data.routes.route.map(route => (
      //           <li >
      //             {this.state.stop.data.routeNo}
      //           </li>
      //       ))}
      //     </ul>
      // );
    }).catch((error)=> {
      this.setState({stop: null, submitted: false});
      console.log(error);
    });
  }

  render() {

    const submitted = this.state.submitted;
    let routeDiv = null;
    if(submitted === true){
      routeDiv = <RouteDiv stop={this.state.stop}/>
    }
    else{
      routeDiv = null;
    }

    return (
        <div>
            <div>
              <form onSubmit={this.handleSubmit}>
                <label>
                  Stop:
                  <input type="text" value={this.state.stopNo} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
              </form>
            </div>
            <div>{routeDiv}</div>
        </div>
    );
  }
}

class RouteDiv extends React.Component{
     // stopNo = props.stop.stopNo;
     // stopDescription = props.stop.stopDescription;

    constructor(props) {
        super(props);
        this.state = {
            stop: props.stop,
            stopNo: props.stop.stopNo,
            stopDescription: props.stop.stopDescription,
            stopSelectedBool: false,
        };

        // this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.handleButton = this.handleButton.bind(this);
    }

    handleButton(event, route) {
        event.preventDefault();
        return (
            <TripsForRouteDiv stopNo={this.state.stopNo} route={route.routeNo}/>
        );
        //const [stop, setStop] = useState([]);
        // if(this.state.stopNo === ''){
        //         //     this.setState({stopNo: 0});
        //         // }
        //         // axios.get("http://localhost:8080/GetRouteSummaryForStopResult/".concat(this.state.stopNo)).then(res => {
        //         //     console.log(res);
        //         //
        //         //     this.setState({stop: res.data, submitted: true});
        //         // }).catch((error)=> {
        //         //     this.setState({stop: null, submitted: false});
        //         //     console.log(error);
        //         // });
    }

     render() {
         if (this.state.stopDescription != null) {
             return (<div><h2>{this.state.stopNo}</h2>
                     <h2>{this.state.stopDescription}</h2>
                     {/*<ul>*/}
                     {this.state.stop.routes.route.map(route => (
                         <button key={route.routeNo}
                                 onClick={this.handleButton(route)}>
                             {route.routeNo} {route.routeHeading}
                         </button>
                     ))}
                     {/*</ul>*/}
                 </div>
             );
         } else {
             return (<div><h1>Invalid stop number</h1></div>
             );
         }
     }
}

function TripsForRouteDiv(props){
    let stop = null;

    axios.get("http://localhost:8080/GetNextTripsForStopResult/".concat(props.stopNo).concat("/").
    concat(props.route)).then(res => {
        console.log(res);
        stop = res.data;
        // this.setState({stop: res.data, submitted: true});

    }).catch((error)=> {
        this.setState({stop: null, submitted: false});
        console.log(error);
    });

    return(
        <div><h2>{stop.stopNo}</h2>
            <h2>{stop.stopDescription}</h2>
            {stop.route.routeDirection.trips.trip.map(trip => (
                <button key={trip.routeNo}>
                    {props.route} {trip.tripDestination} {trip.tripStartTime}
                </button>
            ))}
        </div>
    );

}

function App() {
  return (
    <div className="App">
        <h1>OCTracker</h1>
      {/*<RouteSummaryForStop />*/}
      <StopForm />
      {/*<header className="App-header">*/}
      {/*  <img src={logo} className="App-logo" alt="logo" />*/}
